dom.ready(function(){sb.isset(window.grecaptcha)&&grecaptcha.ready(function(){grecaptcha.execute("6LeDFNMZAAAAAKssrm7yGbifVaQiy1jwfN8zECZZ",{action:"submit"}).then(e=>dom.setValue("#token",e)).catch(dom.showError)}),dom.onChangeInput("#correo",e=>{e.value=sb.lower(e.value)}).onSubmitForm("#contact",o=>{dom.closeAlerts().required("#info","errSendContact").required("#asunto","errSendContact").email("#correo","errSendContact").required("#nombre","errSendContact"),dom.isOk()&&dom.send(o,e=>{dom.showOk(e).val(o.elements,"").setFocus("#nombre")})}),dom.onChangeInput("#usuario",e=>{e.value=sb.toUpperWord(e.value)}).onSubmitForm("#login",e=>dom.closeAlerts().login("#clave","errUserNotFound").user("#usuario","errUserNotFound").isOk())});