dom.ready(function(){const r={loginFormError:"errUserNotFound",contactFormError:"errSendContact",nombre:i18n.required,correo:i18n.email,asunto:i18n.required,info:i18n.required,usuario:i18n.user,clave:i18n.login};sb.isset(window.grecaptcha)&&grecaptcha.ready(function(){grecaptcha.execute("6LeDFNMZAAAAAKssrm7yGbifVaQiy1jwfN8zECZZ",{action:"submit"}).then(o=>dom.setValue("#token",o)).catch(dom.showError)}),dom.onChangeInput("#correo",o=>{o.value=sb.lower(o.value)}).onChangeInput("#usuario",o=>{o.value=sb.toUpperWord(o.value)}).onSubmitForm("#contact",e=>dom.validate(e,r)&&!dom.send(e).then(o=>dom.setOk(e,o))).onSubmitForm("#login",o=>dom.validate(o,r))});